   import { cva } from 'class-variance-authority';
   import { cn } from '@/lib/utils';

   const inputVariants = cva(
     'flex w-full rounded-md border bg-white px-3 py-2 text-sm ring-offset-white file:border-0 file:bg-transparent file:text-sm file:font-medium placeholder:text-ink-400 focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-brand-primary focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50',
     {
       variants: {
         state: {
           default: 'border-sand-200',
           error: 'border-red-500 focus-visible:ring-red-500'
         },
         size: {
           sm: 'h-8 text-xs',
           md: 'h-10 text-sm',
           lg: 'h-12 text-base'
         }
       },
       defaultVariants: {
         state: 'default',
         size: 'md'
       }
     }
   );

   const Input = forwardRef(({ className, state, size, ...props }, ref) => {
     return (
       <input
         className={cn(inputVariants({ state, size, className }))}
         ref={ref}
         {...props}
       />
     );
   });
   Input.displayName = 'Input';

   // Create FormField wrapper component for label, input, and helper text
   const FormField = ({ label, helperText, error, children, className }) => {
     return (
       <div className={cn('space-y-2', className)}>
         {label && (
           <label className="text-sm font-medium leading-none">
             {label}
           </label>
         )}
         {children}
         {(helperText || error) && (
           <p className={cn(
             'text-xs', 
             error ? 'text-red-500' : 'text-ink-500'
           )}>
             {error || helperText}
           </p>
         )}
       </div>
     );
   };

