   ```jsx
   // app/contact/ContactForm.jsx
   'use client';

   import { useState } from 'react';
   import { Button } from '@/components/ui/Button';
   import { Input } from '@/components/ui/Input';
   import { FormField } from '@/components/ui/Input';
   import { Textarea } from '@/components/ui/Textarea';

   export function ContactForm() {
     const [formState, setFormState] = useState({
       name: '',
       email: '',
       message: ''
     });
     const [errors, setErrors] = useState({});
     const [status, setStatus] = useState(null); // 'success', 'error', or null

     const validateForm = () => {
       const newErrors = {};
       if (!formState.name) newErrors.name = 'Name is required';
       if (!formState.email) newErrors.email = 'Email is required';
       else if (!/^\S+@\S+\.\S+$/.test(formState.email)) {
         newErrors.email = 'Please enter a valid email';
       }
       if (!formState.message) newErrors.message = 'Message is required';

       setErrors(newErrors);
       return Object.keys(newErrors).length === 0;
     };

     const handleChange = (e) => {
       const { name, value } = e.target;
       setFormState(prev => ({ ...prev, [name]: value }));
       // Clear error when user types
       if (errors[name]) {
         setErrors(prev => ({ ...prev, [name]: undefined }));
       }
     };

     const handleSubmit = async (e) => {
       e.preventDefault();
       if (!validateForm()) return;

       try {
         // Mock form submission - replace with actual API call
         setStatus('success');
       } catch (error) {
         setStatus('error');
       }
     };

     if (status === 'success') {
       return (
         <div className="bg-green-50 border border-green-200 rounded-lg p-6 text-center">
           <h3 className="text-xl font-bold text-green-800 mb-2">Message Sent!</h3>
           <p className="text-green-700 mb-4">Thank you for contacting us. We'll respond shortly.</p>
           <Button 
             onClick={() => {
               setFormState({ name: '', email: '', message: '' });
               setStatus(null);
             }}
           >
             Send Another Message
           </Button>
         </div>
       );
     }

     if (status === 'error') {
       return (
         <div className="bg-red-50 border border-red-200 rounded-lg p-6 text-center">
           <h3 className="text-xl font-bold text-red-800 mb-2">Something went wrong</h3>
           <p className="text-red-700 mb-4">We couldn't send your message. Please try again later.</p>
           <Button 
             onClick={() => setStatus(null)}
           >
             Try Again
           </Button>
         </div>
       );
     }

     return (
       <form onSubmit={handleSubmit} className="space-y-6">
         <FormField
           label="Name"
           error={errors.name}
         >
           <Input
             name="name"
             value={formState.name}
             onChange={handleChange}
             state={errors.name ? 'error' : 'default'}
             placeholder="Your name"
           />
         </FormField>

         <FormField
           label="Email"
           error={errors.email}
         >
           <Input
             name="email"
             type="email"
             value={formState.email}
             onChange={handleChange}
             state={errors.email ? 'error' : 'default'}
             placeholder="your.email@example.com"
           />
         </FormField>

         <FormField
           label="Message"
           error={errors.message}
         >
           <Textarea
             name="message"
             value={formState.message}
             onChange={handleChange}
             state={errors.message ? 'error' : 'default'}
             placeholder="Your message"
             rows={5}
           />
         </FormField>

         <Button type="submit" className="w-full">
           Send Message
         </Button>
       </form>
     );
   }
   ```
