3. Create a verification script to check all pages:
   ```js
   // scripts/verify-pages.js
   const lighthouse = require('lighthouse');
   const chromeLauncher = require('chrome-launcher');
   const fs = require('fs');
   const path = require('path');

   const urls = [
     'http://localhost:3000',
     'http://localhost:3000/services',
     'http://localhost:3000/about',
     'http://localhost:3000/contact'
   ];

   async function runLighthouse() {
     const chrome = await chromeLauncher.launch({chromeFlags: ['--headless']});
     const options = {port: chrome.port, output: 'json', onlyCategories: ['accessibility', 'performance', 'seo']};

     const results = {};

     for (const url of urls) {
       console.log(`Testing ${url}...`);
       const runnerResult = await lighthouse(url, options);
       const reportJson = runnerResult.report;

       const scores = {
         accessibility: runnerResult.lhr.categories.accessibility.score * 100,
         performance: runnerResult.lhr.categories.performance.score * 100,
         seo: runnerResult.lhr.categories.seo.score * 100
       };

       results[url] = scores;
       console.log(`Results for ${url}:`, scores);

       // Save report
       const pageName = url.split('/').pop() || 'home';
       fs.writeFileSync(
         path.join(__dirname, `../lighthouse-${pageName}.json`),
         reportJson
       );
     }

     await chrome.kill();

     // Save summary
     fs.writeFileSync(
       path.join(__dirname, '../lighthouse-summary.json'),
       JSON.stringify(results, null, 2)
     );

     return results;
   }

   runLighthouse().then(results => {
     console.log('All tests completed!');

     // Check if all pages meet requirements
     let allPassed = true;
     for (const [url, scores] of Object.entries(results)) {
       if (scores.accessibility < 95 || scores.performance < 90 || scores.seo < 90) {
         console.error(`‚ùå ${url} does not meet requirements`);
         allPassed = false;
       } else {
         console.log(`‚úÖ ${url} meets all requirements`);
       }
     }

     process.exit(allPassed ? 0 : 1);
   });
   ```

